let AlertProductName = 'Microsoft Data Loss Prevention (Custom)';
let Workloads = dynamic(['Endpoint', 'SharePoint', 'OneDrive', 'Exchange', 'MicrosoftTeams']);

PurviewDLP(Workloads,true)
| extend Product = AlertProductName
| join kind=leftanti (SecurityAlert
    | where TimeGenerated > ago(12h)
    | where ProductName == AlertProductName
    | extend Identifier = substring(AlertLink, indexof(AlertLink, 'eventid=') + 8, indexof(AlertLink, '&creationtime') - indexof(AlertLink, 'eventid=') - 8)
    ) on Identifier
| order by TimeGenerated asc

let AlertProductName = 'Microsoft Data Loss Prevention (Custom)';
let Workloads = dynamic(['Endpoint']);

let CurrentPolicies = (PurviewDLP_CL
    | where TimeGenerated > ago(90d) and Workload in (Workloads)
    | mv-expand PolicyDetails
    | extend Name = tostring(PolicyDetails.PolicyName)
    | where Name != ""
    | summarize by Name);

let policywatchlist =(_GetWatchlist('Policy')
    | where Workload in (Workloads) and Name in (CurrentPolicies)
    | project SearchKey);

PurviewDLP(Workloads,true)
| where PolicyName != "" //Do Not Remove
| where not(PolicyName has_any (policywatchlist)) //Do not remove
| extend Product = AlertProductName
| order by TimeGenerated

