variables.querySync:
let AlertProductName = "Microsoft Data Loss Prevention (Custom)";

let PolicyWatchlist = _GetWatchlist("Policy")
    | extend Workload = column_ifexists("Workload", ""), Name = column_ifexists("Name", "")
    | where Workload == WorkloadAlias
    | project SearchKey;

PurviewDLP(Workloads, true)
| where PolicyName != "" //Do Not Remove
| where not(PolicyName has_any (PolicyWatchlist)) //Do not remove
| extend Product = AlertProductName
| order by TimeGenerated

variables.queryAll:
let AlertProductName = "Microsoft Data Loss Prevention (Custom)";
let Workloads = dynamic(["Endpoint", "SharePoint", "OneDrive", "Exchange", "MicrosoftTeams"]);

PurviewDLP(Workloads)
| extend Product = AlertProductName
| order by TimeGenerated
